import "core-js/modules/es.array.push.js";
import Header from './components/Header.vue';
import { computed } from 'vue';
export default {
  name: 'app',
  components: {
    Header
  },
  data() {
    return {
      radio: [],
      blog: [],
      loading: true
    };
  },
  provide() {
    return {
      radio: computed(() => this.radio),
      blog: computed(() => this.blog),
      loading: computed(() => this.loading)
    };
  },
  methods: {
    postget() {
      let ths = this;
      axios.get('http://localhost/radio-site/wordpress/wp-json/wp/v2/posts/').then(function (response) {
        for (let i = 0; i < response.data.length; i++) {
          if (response.data[i].categories_info[0].name === "ラジオ") {
            let dates = new Date(response.data[i].date);
            ths.radio.push({
              category: response.data[i].categories_info[0].name,
              title: response.data[i].title.rendered,
              date: dates.getFullYear() + '-' + (dates.getMonth() + 1) + '-' + dates.getDate(),
              thumb: response.data[i].thumbnail_url,
              tag: response.data[i].tags_info[0].name,
              link: '/post/' + response.data[i].id
            });
          } else if (response.data[i].categories_info[0].name === "ブログ") {
            let dates = new Date(response.data[i].date);
            ths.blog.push({
              category: response.data[i].categories_info[0].name,
              title: response.data[i].title.rendered,
              date: dates.getFullYear() + '-' + (dates.getMonth() + 1) + '-' + dates.getDate(),
              thumb: response.data[i].thumbnail_url,
              tag: response.data[i].tags_info[0].name,
              link: '/post/' + response.data[i].id
            });
          }
        }
        ths.loading = false;
      }).catch(function (error) {
        console.log(error);
      });
    }
  }
};
